-- Create camps table
create table camps (
  id uuid primary key default gen_random_uuid(),
  name text not null,
  description text,
  location text not null,
  start_date date not null,
  end_date date not null,
  status text not null check (status in ('Planning', 'Active', 'Completed', 'Cancelled')),
  max_capacity integer not null,
  parent_registration_deadline date,
  created_at timestamptz default now(),
  updated_at timestamptz default now()
);

-- Enable RLS
alter table camps enable row level security;

-- Create policies for camps
create policy "Super admins can view all camps"
  on camps
  for select
  to authenticated
  using (
    (select (auth.jwt() -> 'app_metadata' ->> 'role')::text) = 'super-admin'
  );

create policy "Camp admins can view their assigned camps"
  on camps
  for select
  to authenticated
  using (
    id in (
      select camp_id 
      from camp_staff 
      where user_id = (select auth.uid()) 
      and role = 'camp-admin'
    )
  );

create policy "Staff can view their assigned camps"
  on camps
  for select
  to authenticated
  using (
    id in (
      select camp_id 
      from camp_staff 
      where user_id = (select auth.uid())
    )
  );

create policy "Super admins can insert camps"
  on camps
  for insert
  to authenticated
  with check (
    (select (auth.jwt() -> 'app_metadata' ->> 'role')::text) = 'super-admin'
  );

create policy "Super admins can update all camps"
  on camps
  for update
  to authenticated
  using (
    (select (auth.jwt() -> 'app_metadata' ->> 'role')::text) = 'super-admin'
  );

create policy "Camp admins can update their assigned camps"
  on camps
