  to authenticated
  using (
    id in (
      select camp_id 
      from camp_staff 
      where user_id = (select auth.uid()) 
      and role = 'camp-admin'
    )
  );

-- Create index for performance
create index camps_status_idx on camps(status);
create index camps_dates_idx on camps(start_date, end_date);

-- Create user profiles table to extend auth.users
create table user_profiles (
  id uuid primary key references auth.users(id) on delete cascade,
  email text not null,
  full_name text not null,
  phone text,
  role text not null check (role in ('super-admin', 'camp-admin', 'staff', 'parent')),
  registration_complete boolean default false,
  created_at timestamptz default now(),
  updated_at timestamptz default now()
);

-- Enable RLS
