import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

interface InvitationRequest {
  camperId: string;
  email: string;
  fullName: string;
  relationship: string;
}

Deno.serve(async (req: Request) => {
  // Handle CORS preflight
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    const supabaseClient = createClient(
      Deno.env.get('SUPABASE_URL') ?? '',
      Deno.env.get('SUPABASE_ANON_KEY') ?? '',
      {
        global: {
          headers: { Authorization: req.headers.get('Authorization')! },
        },
      }
    );

    // Verify user is authenticated
    const {
      data: { user },
      error: authError,
    } = await supabaseClient.auth.getUser();

    if (authError || !user) {
      return new Response(
        JSON.stringify({ error: 'Unauthorized' }),
        {
          status: 401,
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        }
      );
    }

    const { camperId, email, fullName, relationship }: InvitationRequest = await req.json();

    // Validate input
    if (!camperId || !email || !fullName || !relationship) {
      return new Response(
        JSON.stringify({ error: 'Missing required fields' }),
        {
          status: 400,
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        }
      );
    }

    // Generate invitation token
    const invitationToken = crypto.randomUUID();
    const expiresAt = new Date();
    expiresAt.setDate(expiresAt.getDate() + 7); // 7 days expiry

    // Create invitation record
    const { data: invitation, error: invitationError } = await supabaseClient
      .from('parent_invitations')
      .insert({
        camper_id: camperId,
        email,
        full_name: fullName,
        relationship,
        invitation_token: invitationToken,
        expires_at: expiresAt.toISOString(),
      })
      .select()
      .single();

    if (invitationError) {
      console.error('Error creating invitation:', invitationError);
      return new Response(
        JSON.stringify({ error: 'Failed to create invitation' }),
        {
          status: 500,
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        }
      );
    }

    // Get camper details for email
    const { data: camper, error: camperError } = await supabaseClient
      .from('campers')
      .select('first_name, last_name, camp:camps(name)')
      .eq('id', camperId)
      .single();

    if (camperError) {
      console.error('Error fetching camper:', camperError);
    }

    // Send invitation email
    const invitationUrl = `https://natively.dev/accept-invitation?token=${invitationToken}`;
    const emailSubject = `CampSync Invitation - ${camper?.camp?.name || 'Camp'}`;
    const emailBody = `
      <h2>You've been invited to CampSync!</h2>
      <p>Hello ${fullName},</p>
      <p>You have been invited to manage information for ${camper?.first_name} ${camper?.last_name} at ${camper?.camp?.name || 'camp'}.</p>
      <p>Click the link below to accept your invitation and set up your account:</p>
      <p><a href="${invitationUrl}">${invitationUrl}</a></p>
      <p>This invitation will expire in 7 days.</p>
      <p>If you did not expect this invitation, please ignore this email.</p>
    `;

    // Note: In production, integrate with an email service like SendGrid, Resend, or AWS SES
    // For now, we'll just log the email details
    console.log('Invitation email:', {
      to: email,
      subject: emailSubject,
      body: emailBody,
    });

    // Create audit log
    await supabaseClient.from('audit_logs').insert({
      user_id: user.id,
      action: 'send_parent_invitation',
      table_name: 'parent_invitations',
      record_id: invitation.id,
      new_data: { email, full_name: fullName, relationship, camper_id: camperId },
    });

    return new Response(
      JSON.stringify({
        success: true,
        invitation,
        message: 'Invitation sent successfully',
      }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      }
    );
  } catch (error) {
    console.error('Error in send-parent-invitation:', error);
    return new Response(
      JSON.stringify({ error: 'Internal server error' }),
      {
        status: 500,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      }
    );
  }
});

import "jsr:@supabase/functions-js/edge-runtime.d.ts";
import { createClient } from 'jsr:@supabase/supabase-js@2';

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

interface CamperImportRow {
  firstName: string;
  lastName: string;
  dateOfBirth: string;
  campId: string;
  sessionId?: string;
  parent1Name?: string;
  parent1Email?: string;
  parent1Phone?: string;
  parent1Relationship?: string;
  parent2Name?: string;
  parent2Email?: string;
  parent2Phone?: string;
  parent2Relationship?: string;
  allergies?: string;
  medications?: string;
  medicalConditions?: string;
  dietaryRestrictions?: string;
  emergencyContact1Name?: string;
  emergencyContact1Phone?: string;
  emergencyContact1Relationship?: string;
  emergencyContact2Name?: string;
  emergencyContact2Phone?: string;
  emergencyContact2Relationship?: string;
}

interface ImportResult {
  success: boolean;
  camperId?: string;
  error?: string;
  row: number;
}

Deno.serve(async (req: Request) => {
  // Handle CORS preflight
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    const supabaseClient = createClient(
      Deno.env.get('SUPABASE_URL') ?? '',
      Deno.env.get('SUPABASE_ANON_KEY') ?? '',
      {
        global: {
          headers: { Authorization: req.headers.get('Authorization')! },
        },
      }
    );

    // Verify user is authenticated and has permission
    const {
      data: { user },
      error: authError,
    } = await supabaseClient.auth.getUser();

    if (authError || !user) {
      return new Response(
        JSON.stringify({ error: 'Unauthorized' }),
        {
          status: 401,
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        }
      );
    }

    const { campers }: { campers: CamperImportRow[] } = await req.json();

    if (!campers || !Array.isArray(campers) || campers.length === 0) {
      return new Response(
        JSON.stringify({ error: 'Invalid or empty campers array' }),
        {
          status: 400,
          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        }
      );
    }

    const results: ImportResult[] = [];
    let successCount = 0;
    let errorCount = 0;

    // Process each camper
    for (let i = 0; i < campers.length; i++) {
      const row = campers[i];
      const rowNumber = i + 1;

      try {
        // Validate required fields
        if (!row.firstName || !row.lastName || !row.dateOfBirth || !row.campId) {
          results.push({
            success: false,
            error: 'Missing required fields (firstName, lastName, dateOfBirth, campId)',
            row: rowNumber,
          });
          errorCount++;
          continue;
        }

        // Create camper
        const { data: camper, error: camperError } = await supabaseClient
          .from('campers')
          .insert({
            first_name: row.firstName,
            last_name: row.lastName,
            date_of_birth: row.dateOfBirth,
            camp_id: row.campId,
            session_id: row.sessionId || null,
            registration_status: 'pending',
            wristband_assigned: false,
            check_in_status: 'not-arrived',
          })
          .select()
          .single();

        if (camperError) {
          results.push({
            success: false,
            error: `Failed to create camper: ${camperError.message}`,
            row: rowNumber,
          });
          errorCount++;
          continue;
        }

        // Add medical info if provided
        if (row.allergies || row.medications || row.medicalConditions || row.dietaryRestrictions) {
          await supabaseClient.from('camper_medical_info').insert({
            camper_id: camper.id,
            allergies: row.allergies ? row.allergies.split(',').map(s => s.trim()) : [],
            medications: row.medications ? row.medications.split(',').map(s => s.trim()) : [],
            medical_conditions: row.medicalConditions ? row.medicalConditions.split(',').map(s => s.trim()) : [],
            dietary_restrictions: row.dietaryRestrictions ? row.dietaryRestrictions.split(',').map(s => s.trim()) : [],
          });
        }

        // Add emergency contacts
        if (row.emergencyContact1Name && row.emergencyContact1Phone) {
          await supabaseClient.from('emergency_contacts').insert({
            camper_id: camper.id,
            full_name: row.emergencyContact1Name,
            phone: row.emergencyContact1Phone,
            relationship: row.emergencyContact1Relationship || 'Parent/Guardian',
            priority_order: 1,
          });
        }

        if (row.emergencyContact2Name && row.emergencyContact2Phone) {
          await supabaseClient.from('emergency_contacts').insert({
            camper_id: camper.id,
            full_name: row.emergencyContact2Name,
            phone: row.emergencyContact2Phone,
            relationship: row.emergencyContact2Relationship || 'Parent/Guardian',
            priority_order: 2,
          });
        }

        // Send parent invitations
        const invitationsToSend = [];

        if (row.parent1Email && row.parent1Name) {
          invitationsToSend.push({
            email: row.parent1Email,
            fullName: row.parent1Name,
            relationship: row.parent1Relationship || 'Parent',
          });
        }

        if (row.parent2Email && row.parent2Name) {
          invitationsToSend.push({
            email: row.parent2Email,
            fullName: row.parent2Name,
            relationship: row.parent2Relationship || 'Parent',
          });
        }

        // Create invitation records
        for (const invitation of invitationsToSend) {
          const invitationToken = crypto.randomUUID();
          const expiresAt = new Date();
          expiresAt.setDate(expiresAt.getDate() + 30); // 30 days for bulk imports

          await supabaseClient.from('parent_invitations').insert({
            camper_id: camper.id,
            email: invitation.email,
            full_name: invitation.fullName,
            relationship: invitation.relationship,
            invitation_token: invitationToken,
            expires_at: expiresAt.toISOString(),
          });

          // Note: In production, queue these emails for batch sending
          console.log(`Invitation queued for ${invitation.email}`);
        }

        results.push({
          success: true,
          camperId: camper.id,
          row: rowNumber,
        });
        successCount++;

      } catch (error) {
        console.error(`Error processing row ${rowNumber}:`, error);
        results.push({
          success: false,
          error: error instanceof Error ? error.message : 'Unknown error',
          row: rowNumber,
        });
        errorCount++;
      }
    }

    // Create audit log
    await supabaseClient.from('audit_logs').insert({
      user_id: user.id,
      action: 'bulk_import_campers',
      table_name: 'campers',
      new_data: {
        total: campers.length,
        success: successCount,
        errors: errorCount,
      },
    });

    return new Response(
      JSON.stringify({
        success: true,
        summary: {
          total: campers.length,
          success: successCount,
          errors: errorCount,
        },
        results,
      }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      }
    );
  } catch (error) {
    console.error('Error in bulk-import-campers:', error);
    return new Response(
      JSON.stringify({ error: 'Internal server error' }),
      {
        status: 500,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      }
    );
  }
});

