-- Create authorization_codes table
create table authorization_codes (
  id uuid primary key default gen_random_uuid(),
  code text unique not null,
  role text not null check (role in ('super-admin', 'camp-admin', 'staff', 'parent')),
  is_active boolean not null default true,
  expires_at timestamptz,
  max_uses integer,
  used_count integer not null default 0,
  linked_camper_ids uuid[] default array[]::uuid[],
  created_by uuid references auth.users(id),
  created_at timestamptz not null default now(),
  updated_at timestamptz not null default now()
);

-- Add index for faster code lookups
create index idx_authorization_codes_code on authorization_codes(code);
create index idx_authorization_codes_is_active on authorization_codes(is_active);

-- Enable RLS
alter table authorization_codes enable row level security;

-- RLS Policies
-- Super admins and camp admins can view all codes
create policy "Super admins and camp admins can view authorization codes"
  on authorization_codes for select
  using (
    exists (
      select 1 from user_profiles
      where user_profiles.id = auth.uid()
      and user_profiles.role in ('super-admin', 'camp-admin')
    )
  );

-- Super admins and camp admins can create codes
create policy "Super admins and camp admins can create authorization codes"
  on authorization_codes for insert
  with check (
    exists (
      select 1 from user_profiles
      where user_profiles.id = auth.uid()
      and user_profiles.role in ('super-admin', 'camp-admin')
    )
  );

-- Super admins and camp admins can update codes
create policy "Super admins and camp admins can update authorization codes"
  on authorization_codes for update
  using (
    exists (
      select 1 from user_profiles
      where user_profiles.id = auth.uid()
      and user_profiles.role in ('super-admin', 'camp-admin')
    )
  );

-- Anyone can validate a code (needed for registration)
-- This is handled through a function with security definer

-- Add email field to emergency_contacts for email-based auto-matching
alter table emergency_contacts add column if not exists email text;

-- Create function to validate authorization code
create or replace function validate_authorization_code(p_code text)
returns jsonb
language plpgsql
security definer
as $$
declare
  v_code_record record;
  v_result jsonb;
begin
  -- Find the code
  select * into v_code_record
  from authorization_codes
  where code = p_code;

  -- Check if code exists
  if not found then
    return jsonb_build_object(
      'valid', false,
      'error', 'Code does not exist'
    );
  end if;

  -- Check if code is active
  if not v_code_record.is_active then
    return jsonb_build_object(
      'valid', false,
      'error', 'Code is not active'
    );
  end if;

  -- Check if code is expired
  if v_code_record.expires_at is not null and v_code_record.expires_at < now() then
    return jsonb_build_object(
      'valid', false,
      'error', 'Code has expired'
    );
  end if;

  -- Check if code has reached max uses
  if v_code_record.max_uses is not null and v_code_record.used_count >= v_code_record.max_uses then
    return jsonb_build_object(
      'valid', false,
      'error', 'Code has reached maximum uses'
    );
  end if;

  -- Code is valid
  return jsonb_build_object(
    'valid', true,
    'role', v_code_record.role,
    'linked_camper_ids', v_code_record.linked_camper_ids,
    'code_id', v_code_record.id
  );
end;
$$;

-- Create function to increment code usage atomically
create or replace function increment_code_usage(p_code_id uuid)
returns void
language plpgsql
security definer
as $$
begin
  update authorization_codes
  set used_count = used_count + 1,
      updated_at = now()
  where id = p_code_id;
end;
$$;

