-- Fix search_path for validate_authorization_code function
create or replace function validate_authorization_code(p_code text)
returns jsonb
language plpgsql
security definer
set search_path = public
as $$
declare
  v_code_record record;
  v_result jsonb;
begin
  -- Find the code
  select * into v_code_record
  from authorization_codes
  where code = p_code;

  -- Check if code exists
  if not found then
    return jsonb_build_object(
      'valid', false,
      'error', 'Code does not exist'
    );
  end if;

  -- Check if code is active
  if not v_code_record.is_active then
    return jsonb_build_object(
      'valid', false,
      'error', 'Code is not active'
    );
  end if;

  -- Check if code is expired
  if v_code_record.expires_at is not null and v_code_record.expires_at < now() then
    return jsonb_build_object(
      'valid', false,
      'error', 'Code has expired'
    );
  end if;

  -- Check if code has reached max uses
  if v_code_record.max_uses is not null and v_code_record.used_count >= v_code_record.max_uses then
    return jsonb_build_object(
      'valid', false,
      'error', 'Code has reached maximum uses'
    );
  end if;

  -- Code is valid
  return jsonb_build_object(
    'valid', true,
    'role', v_code_record.role,
    'linked_camper_ids', v_code_record.linked_camper_ids,
    'code_id', v_code_record.id
  );
end;
$$;

